t = int(input())

for tt in range(t):
    s=input()
    l=set()
    #l.append(s[0])
    n=len(s)
    if n==1:
        print(s,end="")
    else:
        left = 0
        right = 1
        while left<n and right<n:
            if s[left]==s[right]:
                left+=2
                right+=2
            else:
                l.add(s[left])
                left+=1
                right+=1
        if left==n-1:
            l.add(s[left])
    li = list(l)    
    li.sort()
    for i in li:
        print(i,end="")
    print("")

n, m = [int(i) for i in input().split()]
s = {tuple(sorted([int(i) - 1 for i in input().split()])) for i in range(m)}
for i in range(1, n):
    if n % i == 0 and all((n // i) % j for j in range(2, min(n // i, int((n // i) ** 0.5) + 2))):
        if {tuple(sorted([(t[0] + i) % n, (t[1] + i) % n])) for t in s} == s:
            print("Yes")
            break
else:
    print("No")
